import { types } from 'mobx-state-tree';
import { ViewStore } from './ViewStore';
import { User } from './User';
import { getUsers, addUser } from '../api/User';

export const GlobalStore = types
  .model('GlobalStore', {
    viewStore: ViewStore,
    users: types.array(User),
  })
  .actions((self) => ({
    loadSelf() {
      self.viewStore = ViewStore.create()
      self.getUsers()
    },
    loadUsers(response) {
      self.users = []
      const { users } = response
      users.forEach((user) => {
        let newUser = User.create()
        newUser.setUserName(user.userName)
        newUser.setFirstName(user.firstName)
        newUser.setLastName(user.lastName)
        self.users.push(newUser)
      })
    },
    getUsers() {
      let response
      try {
        response = getUsers()
        self.loadUsers(response)
      } catch (error) {
        let message = error
      }
    },
    addUser(firstName, lastName, userName) {
      let response
      try {
        response = addUser(firstName, lastName, userName)
        if (response !== undefined) {
          self.users.push({firstName, lastName, userName})
        }
        self.getUsers()
      } catch (error) {
        let message = error
      }
    }
  }));
